mapper.sql:
where 1=1

and r.r_uid=${rUid }


and u.permission=${roleId }


and u.RealName=#{name }


and case  when #{state }='0' then r.r_state='0'
else r.r_state !='0' end

mapper.select:
select

from t_record r LEFT JOIN t_users u on r.r_uid=u.id

GROUP  BY r.r_uid;

select

from t_record r LEFT JOIN t_users u on r.r_uid=u.ID where TIMESTAMPDIFF(DAY,r.r_bfsj,NOW())<3 

AND r.r_uid=#{rUid}

mapper.delete:
delete from t_record
where id = #{id,jdbcType=INTEGER}
  and r_uid = #{rUid,jdbcType=INTEGER}
